pub enum AlignmentOperation {
    Match(Option<(usize, usize)>),
    Del(Option<(usize, usize)>),
    Ins(Option<usize>),
}

pub enum BackwardSearchResult {
    Complete(Interval),
    Partial(Interval, usize),
    Absent,
}

pub enum Error {
    MissingAt,
    FileOpen {
        path: PathBuf,
        source: io::Error,
    },
    ReadError(io::Error),
    IncompleteRecord,
}

pub enum Error {
    QueryTooShort {
        motif_len: usize,
        query_len: usize,
    },
    InconsistentLen,
    InvalidMonomer {
        mono: u8,
    },
    EmptyMotif,
    NullMotif,
    InvalidPseudos {
        expected: u8,
        received: u8,
    },
}

pub enum Error {
    InvalidDimension {
        an0: usize,
        an1: usize,
        bn: usize,
        bm: usize,
        pin: usize,
    },
}

pub enum Error {
    InvalidProb {
        prob: f64,
    },
}
